SELECT customers.id AS customer_id, customers.first_name AS first_name, customers.last_name AS last_name, customers.email AS email, customers.username AS username, customers.created_at AS joined_at, billing_address.id AS billing_address_id, billing_address.street AS billing_street, billing_address.city AS billing_city, billing_state.code AS billing_state, billing_address.zipcode AS billing_zipcode, shipping_address.id AS shipping_address_id, shipping_address.street AS shipping_street, shipping_address.city AS shipping_city, shipping_state.code AS shipping_state, shipping_address.zipcode AS shipping_zipcode FROM customers